import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import FilteredList from './FilteredList';
import Dropdown from 'react-bootstrap/Dropdown';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { chainPropTypes } from '@mui/utils';
import { formLabelClasses } from '@mui/material';
import { useState } from 'react';

import * as React from 'react';
import Paper from '@mui/material/Paper';
import Divider from '@mui/material/Divider';
import MenuList from '@mui/material/MenuList';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Check from '@mui/icons-material/Check';


export default function NavBar(props) {

  const [allFilters, setAllFilters] = useState(true)

  const handleChange = (event) => {
    // handleForceupdateMethod()
    console.log('event', event)
    console.log('event.target.value', event.target.value)
    props.selectSortStyle(event.target.value);
  };

  function resetFilters () {
    setAllFilters(true)
    props.updateFilters([])
  }

  //sets the state to the selected type, function passed to the onSelect event handler of HTML buttons
  function updateFilters (typetype) {
    var newFilters = []
    const oldFilters = props.filters
    var found = new Boolean(false)


    console.log('oldFilters', oldFilters)
    for (let i = 0; i < oldFilters.length; i++){
      if (oldFilters[i]==typetype) {
        console.log('found')
        found = true
        oldFilters.splice(i,1)
        console.log(oldFilters)
        props.updateFilters(oldFilters)
      }
    }

    if (found == false){
      oldFilters.push(typetype)
      console.log(oldFilters)
      props.updateFilters(oldFilters)
    }

    if(oldFilters.length === 0){
      console.log("NO FILTERS")
      setAllFilters(true)}
    else{
      console.log("FILTERS")
      setAllFilters(false);
    }

  }


  return (
    <div>
      <div>
        <FormControl sx={{ m: 1, minWidth: 120 }} size="small">
          <InputLabel id="demo-simple-select-label">Sort</InputLabel>
          <Select
            value={props.filters}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={"alph"}>Alphabetically</MenuItem>
            <MenuItem value={"lth"}>Price: low to high</MenuItem>
            <MenuItem value={"htl"}>Price: high to low</MenuItem>
            {/* <MenuItem value={30}>Thirty</MenuItem> */}
          </Select>
        </FormControl>
      </div>






    <div>
      <FormControlLabel control={
          <Checkbox 
              checked={allFilters}
              onChange={(event) =>
                resetFilters()
              }
              />
        } label="All" />
      <FormControlLabel control={
          <Checkbox 
              onChange={(event) =>
                updateFilters("Carnivore")
              }
              />
        } label="Carnivore" />
      <FormControlLabel control={
          <Checkbox 
              onChange={(event) =>
                updateFilters("Omnivore")
              }
              />
        } label="Omnivore" />
      <FormControlLabel control={
        <Checkbox 
            onChange={(event) =>
              updateFilters("Herbivore")
            }
            />
      } label="Herbivore" />

        {/* <Nav onSelect={props.selectFilterType}>
          <Nav.Item>Filter:</Nav.Item>
          <Nav.Item><Nav.Link eventKey="All">All</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="5">Over $5</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="5">Under</Nav.Link></Nav.Item>
        </Nav> */}
    </div>


  </div>
  )
}
