import logo from './logo.svg';
import "./App.css";
import { useState } from "react";
import productList from "./assets/products.js";
import Product from "./components/Product.js";
import FilteredList from './components/FilteredList';
import DisplayList from './components/DisplayList'
import NavBar from './components/NavBar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import {useEffect} from 'react'
import Button from '@mui/material/Button';

/*
NEED TO REFRESH GALLERY AFTER UPDATING FILTERS


*/


function App() {
  const [cartItems, setCartItems] = useState([]);
  const [priceTotal, setPriceTotal] = useState(0.00);
  const [filters, setFilters] = useState([]);
  const [sortStyle, setSortStyle] = useState('alph');
  const [filteredListTry, setFilteredListTry] = useState()


  // const handleForceupdateMethod = useForceUpdate();

  useEffect(() => {
    setFilters(filters);
  }, [filters]);

  function buildCart() {
    console.log("CARTITEMS " , cartItems)
    cartItems.map((cartitem, index) => <li>{index + cartitem}</li>)
  }

  //sets the filter state to the selected filters, function passed to the onSelect event handler of HTML buttons
  function selectFilters (eventKey){
    console.log("filter set to " , eventKey)
    setFilters(filters.push(eventKey))

  }

  //sets the sort state to the selected type, function passed to the onSelect event handler of HTML buttons
  function selectSortStyle (eventKey){
    console.log("Sort set to " , eventKey)
    setSortStyle(eventKey)

  }

  //empties cart
  function clearCart () {
    setCartItems([])
    setPriceTotal(0.00)
  }

  function updateFilteredList() {
    console.log('oop')
    // this.forceUpdate
  }

  const myFilteredList = () => {
    return (
      FilteredList({
        list: productList,
        priceTotal: priceTotal, 
        setPriceTotal: setPriceTotal, 
        theCartItems: cartItems, 
        cartSetter: setCartItems,
        setFilters: setFilters,
        filters: filters,

        setSortStyle: setSortStyle,
        sortStyle: sortStyle
      }))
  }


  return (
    <div className="App"> 
      <h1> Welcome to this Store thing</h1>
      <div onChange={updateFilteredList()} className="NavBar">
          {NavBar({
            filters: filters, 
            updateFilters: setFilters,
            selectFilters: selectFilters, 

            // forceUpdateMethod: handleForceupdateMethod,
            selectSortStyle: selectSortStyle,
            sortStyle: sortStyle})}
        </div>

      <div className = "mainApp">

        {console.log("LSIT ", productList)}
        {this.myFilteredList}
        {}

        <div className = "cart">
          <h2>Cart</h2>
          <Button variant="contained" onClick={() => clearCart()}>Clear Cart</Button>
          {cartItems.map((cartitem, index) => <li>{cartitem}</li>)}
          <ul> 
          {buildCart()}
          </ul> 
          {/* TODO: render a list of items in the cart */}
          <h4>Total</h4>
          ${Math.round(priceTotal*100)/100}


        </div>

        {/* </body> */}
      </div>
    </div>
  );
}



export default App;
