import logo from './logo.svg';
import "./App.css";
import { useState } from "react";
import productList from "./assets/products.json";
import Product from "./components/Product.js";
import FilteredList from './components/FilteredList';
import DisplayList from './components/DisplayList'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
// import Button from '@mui/material/Button';


function App() {
  const [cartItems, setCartItems] = useState([]);
  const [priceTotal, setPriceTotal] = useState(0.00);
  const [type, setType] = useState("All");



  return (
    <div className="App">
      {/* <body>  */}
      <Navbar variant="dark" bg="dark" expand="lg">
                <Container fluid>
                  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                  <Navbar.Toggle aria-controls="navbar-dark-example" />
                  <Navbar.Collapse id="navbar-dark-example">
                    <Nav>
                      <NavDropdown
                        id="nav-dropdown-dark-example"
                        title="Dropdown"
                        menuVariant="dark"
                      >
                        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.2">
                          Another action
                        </NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                        <NavDropdown.Divider />
                        <NavDropdown.Item href="#action/3.4">
                          Separated link
                        </NavDropdown.Item>
                      </NavDropdown>
                    </Nav>
                  </Navbar.Collapse>
                </Container>
              </Navbar>
      <div className="NavBar">
        {/* {Navbar} */}
        {/* <Nav onSelect={selectFilterType}>
          <Nav.Item><Nav.Link eventKey="All">All</Nav.Link></Nav.Item>
          </Nav> */}

      </div>
      <div className= "bakeryGallery">
         {productList.map((item,index) => ( // TODO: map bakeryData to BakeryItem components
            // item.price
         Product({
            product: item, 
            priceTotal:priceTotal, 
            setPriceTotal: setPriceTotal, 
            theCartItems: cartItems, 
            cartSetter: setCartItems})

        ))}

      </div>
      <div>
		    <FilteredList list={productList} />
	    </div>

      <div>
         <h2>Cart</h2>
         {cartItems}
         {/* {cartItems.map((cartitem) => <li>{cartitem}</li>)} */}
         {/* TODO: render a list of items in the cart */}
         <h4>Total</h4>
         ${priceTotal}
       </div>
      {/* </body> */}
    </div>
  );
}


