import Product from "./Product.js"
import productList from "../assets/products.json";
import { NavItem } from "react-bootstrap";
//contains filtering/sorting/aggregator methods

export default function FilteredList(props) {


    // this.state = {
	// 	size: "All"
	// };

    const list = props.list 

     //creates a filtering condition

  const matchesFilterType = item => {

    // all items should be shown when no filter is selected
    if(props.type === "All") { 

      return  Product({
           product: item,
           name: item.name, 
           priceTotal: props.priceTotal, 
           setPriceTotal: props.setPriceTotal, 
           theCartItems: props.theCartItems, 
           cartSetter: props.cartSetter})

    } else if (props.type >= item.price) {
      return Product({
        product: item,
        name: item.name, 
        priceTotal: props.priceTotal, 
        setPriceTotal: props.setPriceTotal, 
        theCartItems: props.theCartItems, 
        cartSetter: props.cartSetter})
    } else {
      return false
    }
  }


    return(
        <div className= "bakeryGallery">
        {productList.map((item) => ( // TODO: map bakeryData to BakeryItem components
            matchesFilterType(item) ))}      
     </div>
    )
}
