import logo from './logo.svg';
import "./App.css";
import { useState } from "react";
import productList from "./assets/products.json";
import Product from "./components/Product.js";
import FilteredList from './components/FilteredList';
import DisplayList from './components/DisplayList'
import NavBar from './components/NavBar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';





/*
NEED TO REFRESH GALLERY AFTER UPDATING FILTERS


*/


function App() {
  const [cartItems, setCartItems] = useState([]);
  const [priceTotal, setPriceTotal] = useState(0.00);

  state = {
    filters: [],
    sortStyle: "alph"
  };




  function buildCart() {
    console.log("CARTITEMS " , cartItems)
    cartItems.map((cartitem, index) => <li>{index + cartitem}</li>)
  }

  //sets the filter state to the selected filters, function passed to the onSelect event handler of HTML buttons
  function selectFilters (eventKey){
    console.log("filter set to " , eventKey)
    this.setState({filters: filters.push(eventKey)})
  }

  //sets the sort state to the selected type, function passed to the onSelect event handler of HTML buttons
  function selectSortStyle (eventKey){
    console.log("Sort set to " , eventKey)
    this.setState({sortStyle: setSortStyle(eventKey)})
  }

  //empties cart
  function clearCart () {
    setCartItems([])
    setPriceTotal(0.00)
  }



  return (
    <div className="App"> 
      <h1> Welcome to this Store thing</h1>
      <div className="NavBar">
          {NavBar({
            filters: this.state.filters, 
            updateFilters: this.setFilters,
            selectFilters: this.setFilters, 

            // filterSet: filterSet,
            // setFilterSet: setFilterSet,


            selectSortStyle: this.setSortStyle})}
        </div>

      <div className = "mainApp">

        {FilteredList({
          priceTotal: priceTotal, 
          setPriceTotal: setPriceTotal, 
          theCartItems: cartItems, 
          cartSetter: setCartItems,
          setFilters: this.setFilters,
          filters: this.filters,

          // filterSet: filterSet,
          // setFilterSet: setFilterSet,

          setSortStyle: this.setSortStyle,
          sortStyle: this.sortStyle
        })}


        <div className = "cart">
          <h2>Cart</h2>
          <button onClick={() => clearCart()}>Clear Cart</button>
          {cartItems.map((cartitem, index) => <li>{cartitem}</li>)}
          <ul> 
          {buildCart()}
          </ul> 
          {/* TODO: render a list of items in the cart */}
          <h4>Total</h4>
          ${Math.round(priceTotal*100)/100}


        </div>

        {/* </body> */}
      </div>
    </div>
  );
}



export default App;




