import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import FilteredList from './FilteredList';
import Dropdown from 'react-bootstrap/Dropdown';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { chainPropTypes } from '@mui/utils';
import { formLabelClasses } from '@mui/material';
import { useState } from 'react';


export default function NavBar(props) {

  const [allFilters, setAllFilters] = useState(true)

  const handleChange = (event) => {
    console.log('event', event)
    console.log('event.target.value', event.target.value)
   props.selectSortStyle(event.target.value);
  };


  //sets the state to the selected type, function passed to the onSelect event handler of HTML buttons
  function updateFilters (typetype) {
    const newFilters = []
    const oldFilters = props.filters
    const found = new Boolean(false)

    console.log('updating ', oldFilters)
    for (let i = 0; i < oldFilters.length; i++){
      console.log("it ", i)
      if (oldFilters[i]==typetype) {
        console.log('found')
        found = true
        newFilters = oldFilters.splice(i,1)
        console.log("updating filters ", newFilters)
        props.updateFilters(oldFilters.splice(i,1))
      }
    }

    console.log("after, ", found)

    if (found == false){
      console.log('not found')
      console.log("updating filters ", props.filters)
      console.log("TYPEYTPEYPE", typetype)
      props.updateFilters(oldFilters => [oldFilters, typetype])
    }

    if(props.filters.length === 0){
      console.log("filters, " , props.filters)
      setAllFilters(true)
      console.log("new" , allFilters)}
    else{
      console.log("filters, " , props.filters)
      console.log("false" , allFilters)
      setAllFilters(false);
    }
  }


  return (
    <div>
      <div>
        <FormControl sx={{ m: 1, minWidth: 120 }} size="small">
          <InputLabel id="demo-simple-select-label">Sort</InputLabel>
          <Select
            value={props.filters}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={"alph"}>Alphabetically</MenuItem>
            <MenuItem value={"lth"}>Price: low to high</MenuItem>
            <MenuItem value={"htl"}>Price: high to low</MenuItem>
            {/* <MenuItem value={30}>Thirty</MenuItem> */}
          </Select>
        </FormControl>
      </div>
      <FormControlLabel control={
          <Checkbox 
              checked={allFilters}
              onChange={(event) =>
                updateFilters("")
              }
              />
        } label="All" />
      <FormControlLabel control={
          <Checkbox 
              onChange={(event) =>
                updateFilters("Filter A")
              }
              />
        } label="Filter A" />
      <FormControlLabel control={
          <Checkbox 
              onChange={(event) =>
                updateFilters("Filter B")
              }
              />
        } label="Filter B" />

        {/* <Nav onSelect={props.selectFilterType}>
          <Nav.Item>Filter:</Nav.Item>
          <Nav.Item><Nav.Link eventKey="All">All</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="5">Over $5</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="5">Under</Nav.Link></Nav.Item>
        </Nav> */}
    </div>
  )
}
