import logo from './logo.svg';
import "./App.css";
import { useState } from "react";
import productList from "./assets/products.json";
import Product from "./components/Product.js";
import FilteredList from './components/FilteredList';
import DisplayList from './components/DisplayList'
import NavBar from './components/NavBar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';



function App() {
  const [cartItems, setCartItems] = useState([]);
  const [priceTotal, setPriceTotal] = useState(0.00);
  const [type, setType] = useState("All");


  const filteredData = productList.filter((item) => {return item})

  //@TODO: make this filter function do literally anything at all 
  function myFilterFunction(array){ 
    const filteredArray = array.filter((item) => {
      return item;
      })

    return filteredArray;

  }

  function buildCart() {
    console.log("CARTITEMS " , cartItems)
    cartItems.map((cartitem, index) => <li>{index + cartitem}</li>)
  }

  //sets the state to the selected type, function passed to the onSelect event handler of HTML buttons
  function selectFilterType (eventKey){
    console.log("filter set to " , eventKey)
    setType(eventKey)
  }

  function clearCart () {
    setCartItems([])
  }

  //creates a filtering condition
  const matchesFilterType = item => {
    // all items should be shown when no filter is selected
    if(type === "All") { 
      return true
    } else if (type === item.type) {
      return true
    } else {
      return false
    }
  }




  return (
    <div className="App">

      <div className="NavBar">
        {/* {NavBar()} */}
        <Nav onSelect={selectFilterType}>
          <Nav.Item><Nav.Link eventKey="All">All</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="Some">Some</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="Some Others">Some Others</Nav.Link></Nav.Item>
          </Nav>

      </div>
      {FilteredList({
         priceTotal: priceTotal, 
         setPriceTotal: setPriceTotal, 
         theCartItems: cartItems, 
         cartSetter: setCartItems
      })}
      <div className= "bakeryGallery">
         {filteredData.map((item,index) => ( // TODO: map bakeryData to BakeryItem components
            // item.price
         Product({
            product: item,
            name: item.name, 
            priceTotal:priceTotal, 
            setPriceTotal: setPriceTotal, 
            theCartItems: cartItems, 
            cartSetter: setCartItems})

        ))}

      </div>
      <div>
		    <FilteredList list={productList} />
	    </div>

      <div>
         <h2>Cart</h2>
         {cartItems.map((cartitem, index) => <li>{cartitem}</li>)}
         <ul> 
         {buildCart()}
         </ul> 
         {/* TODO: render a list of items in the cart */}
         <h4>Total</h4>
         ${Math.round(priceTotal*100)/100}


       </div>
       <button onClick={() => clearCart()}>Clear Cart</button>
      {/* </body> */}
    </div>
  );
}



export default App;
