                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">
                    Separated link
                  </NavDropdown.Item> */}
                </NavDropdown>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>

      </div>
      <div className= "bakeryGallery">
         {productList.map((item,index) => ( // TODO: map bakeryData to BakeryItem components
            // item.price
         Product({
            product: item,
            name: item.name, 
            priceTotal:priceTotal, 
            setPriceTotal: setPriceTotal, 
            theCartItems: cartItems, 
            cartSetter: setCartItems})

        ))}

      </div>
      <div>
		    <FilteredList list={productList} />
	    </div>

      <div>
         <h2>Cart</h2>
         {cartItems}
         {/* {cartItems.map((cartitem) => <li>{cartitem}</li>)} */}
         {/* TODO: render a list of items in the cart */}
         <h4>Total</h4>
         ${priceTotal}
       </div>
      {/* </body> */}
    </div>
  );
}


