import cartItems from "../App";
import setCartItems from "../App";
import baboon from '../images/baboon.jpeg'
import buffalo from '../images/baboon.jpeg'
import chicken from '../images/baboon.jpeg'
import duck from '../images/baboon.jpeg'



export default function Product(props) {

    function addToCart(item){
        var newCart = []
        const updatedCart = newCart.concat(props.theCartItems)
        updatedCart.push(props.name)

        props.cartSetter(updatedCart)
        props.setPriceTotal(props.priceTotal+props.product.price)
    }

    return(

            <div class="Product"> 
                {/* <img src={new URL("../images/baboon.jpeg", import.meta.url)} /> */}
                {/* <img src={props.product.image} /> */}
                {console.log(props.product.name.toLowerCase())}
                <img src={props.product.name.toLowerCase().toString} />
                <h2> {props.product.name}</h2>
                <p>{props.product.description}</p>
                <p>${props.product.price}</p>
                <button onClick={() => addToCart()}>Add to Cart</button>
            </div> 


    )
}
